package app.persistence;import javax.persistence.EntityManagerFactory;import javax.persistence.Persistence;/** * @author iwaszkiewicz * @date 02.03.2010 */public class GAE_EntityManagerFactory {    private String persistenceUnit = null;    private static EntityManagerFactory EMF = null;    public GAE_EntityManagerFactory(String persistenceUnit) {        this.persistenceUnit = persistenceUnit;    }    public EntityManagerFactory entityManagerFactory() {        synchronized (GAE_EntityManagerFactory.class) {            if (EMF == null) {                EMF = Persistence.createEntityManagerFactory(this.persistenceUnit);            }        }        return EMF;    }}